import React from "react";
//import "./Coin.css";

function Coin({ name, icon, price, symbol, marketCap, priceChange }) {
  return (
    <div className="coin">
      <img src={icon} alt={`${name} icon`} />
      <h1>{name}<br/>{symbol.toUpperCase()}</h1>
      <h3>Current Price<br/>:${price.toLocaleString()}</h3>
      <h3>Market Cap:<br/>${marketCap.toLocaleString()}</h3>
      <h3> Change:<br/>(){priceChange.toFixed(2)}%</h3>
    </div>
  );
}

export default Coin;
