import React from "react";
//import "./Coin.css";

function Coin({ name, icon, price, symbol, marketCap, priceChange }) {
  return (
    <div className="coin">
      <h1>Name: {name}</h1>
      <img src={icon} alt={`${name} icon`} />
      <h1>Name: {name}</h1>
      <h3>Price: ${price.toLocaleString()}</h3>
      <h3>Symbol: {symbol.toUpperCase()}</h3>
      <h3>Market Cap: ${marketCap.toLocaleString()}</h3>
      <h3>24h Change: {priceChange.toFixed(2)}%</h3>
    </div>
  );
}

export default Coin;
